  //   title: "Galeri 7",
  //   author: "ba",
  // },
  // {
  //   img: "https://picsum.photos/id/0/367/267",
  //   title: "Galeri 8",
  //   author: "ba",
  // },
  // {
  //   img: "https://picsum.photos/id/0/367/267",
  //   title: "Galeri 9",
  //   author: "ba",
  // },
  // {
  //   img: "https://picsum.photos/id/0/367/267",
  //   title: "Galeri 10",
  //   author: "ba",
  // },
];

export default function ManageGaleri() {
  const classes = useStyles();

  return (
    <Grid container spacing={3}>
      {tileData.map((item) => {
        return (
          <Grid item xs={12} sm={6}>
            <Card className={classes.root}>
              <CardMedia
                className={classes.media}
                image={item.img}
                title={item.title}
              />
              <CardContent>
                <Typography variant="body2" color="textSecondary" component="p">
                  {item.title}
                </Typography>
              </CardContent>
              <CardActions className={classes.cardaction} disableSpacing>
                <Box
                  display="flex"
                  flexDirection="column"
                  className={classes.box}
                >
                  <input type="file" className={classes.input} />
                  <Button
                    color="primary"
                    variant="contained"
                    onClick={() => tileData.push(item)}
                  >
                    Upload
                  </Button>
                </Box>
                <Box
                  display="flex"
                  flexDirection="column"
                  className={classes.box}
                >
                  <input type="file" className={classes.input} />
                  <Button color="info" variant="contained">
                    Update
                  </Button>
                </Box>
              </CardActions>
            </Card>
          </Grid>
        );
      })}
    </Grid>
  );
}
