import React, { useState } from "react";
import {
  Card,
  CardHeader,
  CardContent,
  CardActions,
  Divider,
  Avatar,
  Button,
  TextField,
  Typography,
} from "@material-ui/core";
import { makeStyles } from "@material-ui/styles";
import { apiDashManage } from "../../../api/api";
import { ArticleContext } from "../../../context/articleContext";
import Swal from "sweetalert2";
import CKEditor from "@ckeditor/ckeditor5-react";
import ClassicEditor from "@ckeditor/ckeditor5-build-classic";
import CreatableSelect from "react-select/creatable";
import makeAnimated from "react-select/animated";
import axios from "axios";

const customStyles = {
  container: () => ({
    marginBottom: "1rem",
    marginTop: "1rem",
  }),
  menu: () => ({
    width: "100%",
  }),
};

const animatedComponents = makeAnimated();

const useStyles = makeStyles((theme) => ({
  root: {},
  details: {
    display: "flex",
  },
  avatar: {
    marginLeft: "auto",
    height: 110,
    width: 100,
    flexShrink: 0,
    flexGrow: 0,
  },
  progress: {
    marginTop: theme.spacing(2),
  },
  uploadButton: {
    marginRight: theme.spacing(2),
  },
}));

const ManageArticle = () => {
  const classes = useStyles();

  const [values, setValues] = useState({
    title: "",
    tags: [],
    description: "",
    image: null,
    imgrequest: null,
    picture: "https://placehold.it/500x600",
    figurethumbnails: "",
  });
  const [loading, setLoading] = useState(true);
  const [loadArticle, setLoadArtile] = useState(false);
  const [article, setArticle] = React.useContext(ArticleContext);

  const handleChange = (event) => {
    setValues({
      ...values,
      [event.target.name]: event.target.value,
    });
  };

  const handleTags = (newValue) => {
    let filter;

    filter = newValue.filter((el) => {
      return delete el.__isNew__;
    });

    setValues((values) => ({
      ...values,
      tags: filter,
    }));
  };

  const onImageChange = (e) => {
    e.preventDefault();
    let reader = new FileReader();
    let file = e.target.files[0];

    reader.onloadend = () => {
      setLoading(false);
      setValues((values) => ({
        ...values,
        image: file,
        picture: reader.result,
        figurethumbnails: file.name,
      }));
    };

    reader.readAsDataURL(file);
  };

  const addImage = () => {
    console.log(values.image);
    const formdata = new FormData();
    formdata.append("image", values.image);

    console.log(formdata);
    axios({
      method: "post",
      url: `${apiDashManage + "image"}`,
      headers: {
        Authorization: "Bearer " + localStorage.getItem("token"),
      },
      data: formdata,
    })
      .then((res) => {
        Swal.fire("Good job!", "Update success", "success");
        setValues((values) => ({
          ...values,
          imgrequest: res.data.thumbnail,
        }));
      })
      .catch((err) => {
        Swal.fire({
          icon: "error",
          title: "Check your connections",
          text: "",
        });
      });
  };

  const addArticle = (e) => {
    e.preventDefault();
    setLoadArtile(true);

    // const formData = new FormData();
    // for (let i = 0; i < values.tags.length; i++) {
    //   const arr = values.tags[i];
    //   formData.append("tags", JSON.stringify(arr));
    // }
    // formData.append("title", values.title);
    // formData.append("description", values.description);

    // let tags;

    // for (let i = 0; i < values.tags.length; i++) {
    //   const arr = values.tags[i];
    //   tags = JSON.stringify(arr);
    // }

    // console.log(tags);

    console.log(values.tags);

    const dataArticle = {
      title: values.title,
      tags: values.tags,
      image: values.imgrequest,
      description: values.description,
    };

    axios({
      method: "post",
      url: `${apiDashManage + "article"}`,
      headers: {
        Authorization: `Bearer ${localStorage.getItem("token")}`,
      },
      data: dataArticle,
    })
      .then((res) => {
        setLoadArtile(false);
        Swal.fire("Success !", "Article has been created", "success");

        setValues((values) => ({
          ...values,
          title: "",
          tags: [],
          description: "",
          image: "",
          picture: "",
          imgrequest: null,
          figurethumbnails: "",
        }));

        console.log(res);

        console.log(values.imgrequest);
      })
      .catch((err) => {
        setLoadArtile(false);
        Swal.fire({
          icon: "error",
          title: "Check your connections",
          text: "",
        });
        setValues((values) => ({
          ...values,
          title: "",
          tags: [],
          description: "",
          image: "",
          picture: "",
          imgrequest: null,
          figurethumbnails: "",
        }));

        console.log(values.imgrequest);

        console.log(err);
      });
  };

  return (
    <>
      <Card>
        <CardHeader subheader="Manage your Article" title="Article" />
        <Divider />
        <form id="myform" encType="multipart/form-data">
          <CardContent>
            <div className={classes.details}>
              <Avatar className={classes.avatar} src={values.picture} />
              <input
                style={{
                  position: "absolute",
                  marginTop: "40px",
                  cursor: "pointer",
                }}
                type="file"
                onChange={onImageChange}
                name="image"
              />
            </div>
          </CardContent>
          <Divider />
          <CardActions>
            <Button
              onClick={addImage}
              className={classes.uploadButton}
              color="primary"
              variant="text"
            >
              Upload Thumbnail
            </Button>
          </CardActions>
        </form>

        <CardContent>
          <TextField
            fullWidth
            label="Title"
            name="title"
            onChange={handleChange}
            type="text"
            value={values.title}
            variant="outlined"
          />

          <CreatableSelect
            isMulti
            onChange={handleTags}
            components={animatedComponents}
            styles={customStyles}
            placeholder="Add more tags..."
          />
          <CKEditor
            editor={ClassicEditor}
            onChange={(event, editor) => {
              const data = editor.getData();
              setValues((values) => ({
                ...values,
                description: data,
              }));
            }}
          />
          {/* <form encType="multipart/form-data">
            <Button
              color="primary"
              variant="outlined"
              component="label"
              style={{ marginTop: "1rem" }}
            >
              Upload Thumbnail
              <input
                type="file"
                name="image"
                style={{ display: "none" }}
                onChange={onImageChange}
              />
            </Button>
            <Typography variant="body1" style={{ marginTop: 25 }}>
              {loading ? "No file choosen" : values.figurethumbnails}
            </Typography>
          </form> */}
        </CardContent>
        <Divider />
        <CardActions style={{ justifyContent: "center" }}>
          <Button
            color="primary"
            variant="outlined"
            onClick={addArticle}
            disabled={loadArticle}
          >
            {loadArticle ? "Loading ..." : "Add Article"}
          </Button>
        </CardActions>
      </Card>
    </>
  );
};

export default ManageArticle;
