import React from "react";
import { apiDashManage } from "../api/api";
import axios from "axios";

let ContextType;
const { Provider, Consumer } = (ContextType = React.createContext());

class TentangProvider extends React.Component {
  state = {
    tentangkami: [],
    snackbar: {
      loadTentang: true,
      open: false,
      title: "",
      severity: "",
      vertical: "top",
      horizontal: "right",
    },
    values: {
      description: "",
      type: "Tentang-Kami",
    },
    loading: false,
  };

  componentDidMount() {
    this.getTentangKami();
  }

  getTentangKami = () => {
    axios
      .get(`${apiDashManage}tentang-kami`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
      })
      .then((res) => {
        if (res.data.portalWebs.length <= 0) {
          this.setState((prevState) => ({
            snackbar: {
              ...prevState.snackbar,
              open: true,
              title: "Your tentang-kami empty, please make first description !",
              severity: "info",
              loadTentang: false,
            },
          }));
        } else {
          console.log(this.state.tentangkami);
          this.setState((prevState) => ({
            tentangkami: res.data.portalWebs,
            snackbar: {
              ...prevState.snackbar,
              open: false,
              loadTentang: false,
            },
          }));
        }
      })
      .catch((err) => console.log(err));
  };

  handleChange = (event, editor) => {
    const data = editor.getData();
    this.setState(
      (prevState) => ({
        values: {
          ...prevState.values,
          description: data,
        },
      }),
      () => console.log(this.state.values.description)
    );
  };

  addTentang = () => {
    this.setState({
      loading: true,
    });

    const dataTentang = {
      description: this.state.values.description,
      type: this.state.values.type,
    };

    axios({
      method: "post",
      url: `${apiDashManage}portal-web`,
      headers: {
        Authorization: `Bearer ${localStorage.getItem("token")}`,
      },
      data: dataTentang,
    })
      .then(() => {
        this.setState((prevState) => ({
          loading: false,
          snackbar: {
            ...prevState.snackbar,
            open: true,
            title: "Description has been created !",
            severity: "success",
          },
        }));
      })
      .catch(() => {
        this.setState((prevState) => ({
          loading: false,
          snackbar: {
            ...prevState.snackbar,
            open: true,
            title: "Connection Error !",
            severity: "error",
          },
        }));
      });
  };

  deleteTentang = async (id) => {
    this.setState({
      loading: true,
    });
    const response = await axios({
      method: "delete",
      url: `${apiDashManage}tentang-kami/${id}`,
      headers: {
        Authorization: `Bearer ${localStorage.getItem("token")}`,
      },
    });

    const responseJson = await response;

    if (responseJson) {
      this.setState((prevState) => ({
        loading: false,

        snackbar: {
          ...prevState.snackbar,
          open: true,
          title: "Description has been deleted !",
          severity: "success",
        },
      }));

      this.getTentangKami();
    } else {
      this.setState((prevState) => ({
        loading: false,

        snackbar: {
          ...prevState.snackbar,
          open: true,
          title: "Check your connection !",
          severity: "error",
        },
      }));
    }
  };

  onClose = () => {
    this.setState((prevState) => ({
      snackbar: {
        ...prevState.snackbar,
        open: false,
      },
    }));
  };

  render() {
    const { state, onClose, deleteTentang, handleChange } = this;
    const { children } = this.props;
    return (
      <Provider
        value={{
          ...state,
          onClose: onClose,
          deleteTentang: deleteTentang,
          handleChange: handleChange,
        }}
      >
        {children}
      </Provider>
    );
  }
}

export { Provider, Consumer, TentangProvider };
