import React, { useState } from "react";
import {
  Card,
  CardHeader,
  CardContent,
  CardActions,
  Divider,
  Button,
  TextField,
  Typography,
} from "@material-ui/core";
import { apiDashManage } from "../../../api/api";
import { ArticleContext } from "../../../context/articleContext";
import Swal from "sweetalert2";
import CKEditor from "@ckeditor/ckeditor5-react";
import ClassicEditor from "@ckeditor/ckeditor5-build-classic";
import CreatableSelect from "react-select/creatable";
import makeAnimated from "react-select/animated";
import axios from "axios";

const customStyles = {
  container: () => ({
    marginBottom: "1rem",
    marginTop: "1rem",
  }),
  menu: () => ({
    width: "100%",
  }),
};

const animatedComponents = makeAnimated();

const ManageArticle = () => {
  const [values, setValues] = useState({
    title: "",
    tags: [],
    description: "",
    image: null,
    figurethumbnails: "",
  });
  const [loading, setLoading] = useState(true);
  const [loadArticle, setLoadArtile] = useState(false);
  const [article, setArticle] = React.useContext(ArticleContext);

  const handleChange = (event) => {
    setValues({
      ...values,
      [event.target.name]: event.target.value,
    });
  };

  const handleTags = (newValue) => {
    setValues((values) => ({
      ...values,
      tags: newValue,
    }));
  };

  const onImageChange = (e) => {
    e.preventDefault();
    let reader = new FileReader();
    let file = e.target.files[0];

    reader.onloadend = () => {
      setLoading(false);
      setValues((values) => ({
        ...values,
        image: file,
        figurethumbnails: file.name,
      }));
    };

    reader.readAsDataURL(file);
  };

  const addArticle = (e) => {
    e.preventDefault();
    setLoadArtile(true);

    const formData = new FormData();
    for (let i = 0; i < values.tags.length; i++) {
      const arr = values.tags[i];
      formData.append("tags", JSON.stringify(arr));
    }
    formData.append("title", values.title);
    formData.append("description", values.description);
    formData.append("image", values.image);

    const data = localStorage.getItem("data");
    const parse = JSON.parse(data);

    axios({
      method: "post",
      url: `${apiDashManage}article`,
      headers: {
        Authorization: `Bearer ${parse._id}`,
      },
      data: formData,
    })
      .then((res) => {
        setLoadArtile(false);
        Swal.fire("Success !", "Article has been created", "success");

        setValues((values) => ({
          ...values,
          title: "",
          tags: [],
          description: "",
          image: null,
          figurethumbnails: "",
        }));
        console.log(res);

        console.log(formData);
      })
      .catch((err) => {
        setLoadArtile(false);
        Swal.fire({
          icon: "error",
          title: "Check your connections",
          text: "",
        });
        setValues((values) => ({
          ...values,
          title: "",
          tags: [],
          description: "",
          image: null,
          figurethumbnails: "",
        }));
        console.log(err);
        console.log(formData);
      });

    console.log(values);
  };

  return (
    <>
      <Card>
        <CardHeader subheader="Manage your Article" title="Article" />
        <Divider />
        <CardContent>
          <TextField
            fullWidth
            label="Title"
            name="title"
            onChange={handleChange}
            type="text"
            value={values.title}
            variant="outlined"
          />

          <CreatableSelect
            isMulti
            onChange={handleTags}
            components={animatedComponents}
            styles={customStyles}
            placeholder="Add more tags..."
          />
          <CKEditor
            editor={ClassicEditor}
            onChange={(event, editor) => {
              const data = editor.getData();
              setValues((values) => ({
                ...values,
                description: data,
              }));
            }}
          />
          <form encType="multipart/form-data">
            <Button
              color="primary"
              variant="outlined"
              component="label"
              style={{ marginTop: "1rem" }}
            >
              Upload Thumbnail
              <input
                type="file"
                name="image"
                style={{ display: "none" }}
                onChange={onImageChange}
              />
            </Button>
            <Typography variant="body1" style={{ marginTop: 25 }}>
              {loading ? "No file choosen" : values.figurethumbnails}
            </Typography>
          </form>
        </CardContent>
        <Divider />
        <CardActions style={{ justifyContent: "center" }}>
          <Button
            color="primary"
            variant="outlined"
            onClick={addArticle}
            disabled={loadArticle}
          >
            {loadArticle ? "Loading ..." : "Add Article"}
          </Button>
        </CardActions>
      </Card>
    </>
  );
};

export default ManageArticle;
