import React from "react";
import { apiDashManage } from "../api/api";
import axios from "axios";

let ContextType;
const { Provider, Consumer } = (ContextType = React.createContext());

class TentangProvider extends React.Component {
  state = {
    tentangkami: [],
  };

  componentDidMount() {
    this.getTentangKami();
  }

  getTentangKami = () => {
    axios
      .get(`${apiDashManage}tentang-kami`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
      })
      .then((res) => {
        this.setState({
          tentangkami: res.portalWebs,
        });
      });
  };

  render() {
    const { state } = this;
    const { children } = this.props;
    return (
      <Provider
        value={{
          ...state,
        }}
      >
        {children}
      </Provider>
    );
  }
}

export { Provider, Consumer, TentangProvider };
