import React, { useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import { Instagram } from "react-content-loader";
import { red } from "@material-ui/core/colors";
import { apiDashManage } from "../../../api/api";
import { ArticleContext } from "../../../context/articleContext";
import Card from "@material-ui/core/Card";
import CardHeader from "@material-ui/core/CardHeader";
import CardMedia from "@material-ui/core/CardMedia";
import CardContent from "@material-ui/core/CardContent";
import CardActions from "@material-ui/core/CardActions";
import Avatar from "@material-ui/core/Avatar";
import Typography from "@material-ui/core/Typography";
import Grid from "@material-ui/core/Grid";
import IconButton from "@material-ui/core/IconButton";
import Container from "@material-ui/core/Container";
import CreateIcon from "@material-ui/icons/Create";
import Button from "@material-ui/core/Button";
import Swal from "sweetalert2";
import axios from "axios";
import moment from "moment";
import Edit from "./Edit";
import EmptyArticle from "./EmptyArticle";

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  media: {
    height: 0,
    paddingTop: "56.25%",
  },
  expand: {
    transform: "rotate(0deg)",
    marginLeft: "auto",
    transition: theme.transitions.create("transform", {
      duration: theme.transitions.duration.shortest,
    }),
  },
  expandOpen: {
    transform: "rotate(180deg)",
  },
  avatar: {
    backgroundColor: red[500],
  },
  col: {
    [theme.breakpoints.down("sm")]: {
      maxWidth: "100%",
      flexBasis: "100%",
    },
  },
  imgResponsive: {
    width: 450,
    height: 200,
    [theme.breakpoints.down("sm")]: {
      width: "100%",
    },
  },
  cardLayout: {
    height: "auto",
    [theme.breakpoints.down("sm")]: {
      height: "auto",
      overflow: "hidden",
    },
  },
}));

export default function Item() {
  const classes = useStyles();
  const [article, setArticle] = React.useContext(ArticleContext);
  const [loading, setLoading] = React.useState(false);
  const [loadingArticle, setLoadingArticle] = React.useState(false);
  const [open, setOpen] = React.useState(false);
  const data = localStorage.getItem("data");
  const parse = JSON.parse(data);

  const user = {
    name: parse.username,
    avatar: `http://dashmanage.herokuapp.com/${parse.picture}`,
  };

  useEffect(() => {
    const fetchArticle = async () => {
      setLoadingArticle(true);
      const response = await fetch(`${apiDashManage}article`);
      const responseJson = await response.json();
      setLoadingArticle(false);
      setArticle(responseJson.articles);
    };
    fetchArticle();
  }, []);

  const deleteArticle = (id) => {
    setLoading(true);
    axios
      .delete(`${apiDashManage}article/${id}`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
      })
      .then(() => {
        setLoading(false);
        Swal.fire({
          icon: "success",
          title: "Article has been deleted !",
        });
      })
      .catch(() => {
        setLoading(false);
        Swal.fire({
          icon: "error",
          title: "Check your connections",
          text: "",
        });
      });
  };

  return (
    <div className={classes.root}>
      <Container>
        <Grid container spacing={3}>
          {() => {
            if (article.length >= 0) {
              return article.map((tile) => {
                return (
                  <Grid className={classes.col} item xs={6} sm={3}>
                    <Card key={tile._id} className={classes.cardLayout}>
                      <CardHeader
                        avatar={
                          <Avatar
                            aria-label="recipe"
                            src={user.avatar}
                            className={classes.avatar}
                          />
                        }
                        action={
                          <IconButton
                            aria-label="settings"
                            onClick={() => setOpen(true)}
                          >
                            <CreateIcon />
                          </IconButton>
                        }
                        title={`${tile.title}`}
                        subheader={moment(tile.createdAt).format("LLL")}
                      />
                      <CardMedia
                        className={classes.media}
                        image={`http://dashmanage.herokuapp.com/${tile.thumbnail}`}
                        title={tile.title}
                      />
                      <CardContent>
                        <Typography
                          variant="body2"
                          color="textSecondary"
                          component="p"
                          dangerouslySetInnerHTML={{
                            __html: `${tile.description}`,
                          }}
                        />
                      </CardContent>
                      <CardActions disableSpacing>
                        <Button
                          variant="outlined"
                          color="secondary"
                          disabled={loading}
                          onClick={() => deleteArticle(tile._id)}
                        >
                          {loading ? "Loading ..." : "DELETE"}
                        </Button>
                      </CardActions>
                    </Card>
                  </Grid>
                );
              });
            } else {
              return <EmptyArticle />;
            }
          }}
        </Grid>

        <Edit open={open} handleClose={() => setOpen(false)} />
      </Container>
    </div>
  );
}
