import React, { useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import { red } from "@material-ui/core/colors";
import { apiDashManage } from "../../../api/api";
import { ArticleContext } from "../../../context/articleContext";
import Snackbar from "@material-ui/core/Snackbar";
import MuiAlert from "@material-ui/lab/Alert";
import Card from "@material-ui/core/Card";
import CardHeader from "@material-ui/core/CardHeader";
import CardMedia from "@material-ui/core/CardMedia";
import CardContent from "@material-ui/core/CardContent";
import CardActions from "@material-ui/core/CardActions";
import Avatar from "@material-ui/core/Avatar";
import Typography from "@material-ui/core/Typography";
import Grid from "@material-ui/core/Grid";
import IconButton from "@material-ui/core/IconButton";
import Container from "@material-ui/core/Container";
import CreateIcon from "@material-ui/icons/Create";
import Button from "@material-ui/core/Button";
import Swal from "sweetalert2";
import axios from "axios";
import moment from "moment";
import Edit from "./Edit";

function Alert(props) {
  return <MuiAlert elevation={6} variant="filled" {...props} />;
}

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  media: {
    height: 0,
    paddingTop: "56.25%",
  },
  expand: {
    transform: "rotate(0deg)",
    marginLeft: "auto",
    transition: theme.transitions.create("transform", {
      duration: theme.transitions.duration.shortest,
    }),
  },
  expandOpen: {
    transform: "rotate(180deg)",
  },
  avatar: {
    backgroundColor: red[500],
  },
  col: {
    [theme.breakpoints.down("sm")]: {
      maxWidth: "100%",
      flexBasis: "100%",
    },
  },
  imgResponsive: {
    width: 450,
    height: 200,
    [theme.breakpoints.down("sm")]: {
      width: "100%",
    },
  },
  cardLayout: {
    height: "auto",
    [theme.breakpoints.down("sm")]: {
      height: "auto",
      overflow: "hidden",
    },
  },
}));

export default function Item() {
  const classes = useStyles();
  const [article, setArticle] = React.useContext(ArticleContext);
  const [loading, setLoading] = React.useState(false);
  const [state, setState] = React.useState({
    open: false,
    vertical: "top",
    horizontal: "right",
  });
  const [open, setOpen] = React.useState(false);
  const data = localStorage.getItem("data");
  const parse = JSON.parse(data);

  const user = {
    name: parse.username,
    avatar: `http://dashmanage.herokuapp.com/${parse.picture}`,
  };

  useEffect(() => {
    const fetchArticle = async () => {
      const response = await axios({
        method: "get",
        url: `${apiDashManage}article`,
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
      });
      const responseJson = await response;
      if (responseJson.error) {
        setState((state) => ({
          ...state,
          open: true,
        }));
      } else {
        setState((state) => ({
          ...state,
          open: false,
        }));
        setArticle(responseJson.data.articles);
      }
    };
    fetchArticle();
  }, []);

  const deleteArticle = (id) => {
    setLoading(true);
    axios
      .delete(`${apiDashManage}article/${id}`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
      })
      .then(() => {
        setLoading(false);
        Swal.fire({
          icon: "success",
          title: "Article has been deleted !",
        });
      })
      .catch(() => {
        setLoading(false);
        Swal.fire({
          icon: "error",
          title: "Check your connections",
          text: "",
        });
      });
  };

  const { horizontal, vertical } = state;

  return (
    <div className={classes.root}>
      <Container>
        <Snackbar
          anchorOrigin={{ vertical, horizontal }}
          open={state.open}
          autoHideDuration={6000}
          onClose={() =>
            setState((state) => ({
              ...state,
              open: false,
            }))
          }
          key={vertical + horizontal}
        >
          <Alert
            onClose={() =>
              setState((state) => ({
                ...state,
                open: false,
              }))
            }
            severity="info"
          >
            Artikel mafiii, silahkan buat satu artikel !
          </Alert>
        </Snackbar>

        <Grid container spacing={3}>
          {article.map((tile) => {
            return (
              <Grid className={classes.col} item xs={6} sm={3}>
                <Card key={tile._id} className={classes.cardLayout}>
                  <CardHeader
                    avatar={
                      <Avatar
                        aria-label="recipe"
                        src={user.avatar}
                        className={classes.avatar}
                      />
                    }
                    action={
                      <IconButton
                        aria-label="settings"
                        onClick={() => setOpen(true)}
                      >
                        <CreateIcon />
                      </IconButton>
                    }
                    title={`${tile.title}`}
                    subheader={moment(tile.createdAt).format("LLL")}
                  />
                  {/* <CardMedia
                    className={classes.media}
                    title={tile.title}
                  /> */}
                  <img src={`${tile.thumbnail.imageUrl}`} alt={tile.title} />

                  <CardContent>
                    <Typography
                      variant="body2"
                      color="textSecondary"
                      component="p"
                      dangerouslySetInnerHTML={{
                        __html: `${tile.description}`,
                      }}
                    />
                  </CardContent>
                  <CardActions disableSpacing>
                    <Button
                      variant="outlined"
                      color="secondary"
                      disabled={loading}
                      onClick={() => deleteArticle(tile._id)}
                    >
                      {loading ? "Loading ..." : "DELETE"}
                    </Button>
                  </CardActions>
                </Card>
              </Grid>
            );
          })}
        </Grid>

        <Edit open={open} handleClose={() => setOpen(false)} />
      </Container>
    </div>
  );
}
