import React from "react";
import { apiDashManage } from "../api/api";
import axios from "axios";

let ContextType;
const { Provider, Consumer } = (ContextType = React.createContext());

class ArticleProvider extends React.Component {
  state = {
    article: [],
    snackbar: {
      loadArticle: true,
      open: false,
      title: "",
      severity: "",
      vertical: "top",
      horizontal: "right",
    },
    loading: false,
  };

  componentDidMount() {
    this.fetchArticle();
  }

  fetchArticle = async () => {
    const response = await axios({
      method: "get",
      url: `${apiDashManage}article`,
      headers: {
        Authorization: `Bearer ${localStorage.getItem("token")}`,
      },
    });
    const responseJson = await response;
    if (responseJson.error) {
      // setState((state) => ({
      //   ...state,
      //   open: true,
      //   title: "Your article empty , please make first article !",
      //   severity: "info",
      // }));
      this.setState({
        snackbar: {
          open: true,
          title: "Your article empty, please make first article !",
          severity: "info",
        },
      });
    } else {
      // setState((state) => ({
      //   ...state,
      //   open: false,
      //   loadArticle: false,
      // }));
      // setArticle(responseJson.data.articles);
      this.setState({
        article: responseJson.data.articles,
        snackbar: {
          open: false,
          loadArticle: false,
        },
      });
    }
  };

  onClose = () => {
    this.setState({
      snackbar: {
        open: false,
      },
    });
  };

  deleteArticle = (id) => {
    this.setState({
      loading: true,
    });
    axios
      .delete(`${apiDashManage}article/${id}`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
      })
      .then(() => {
        this.setState({
          loading: true,
        });
        this.setState({
          snackbar: {
            open: true,
            title: "Article has been deleted !",
            severity: "success",
          },
        });

        this.fetchArticle();
      })
      .catch(() => {
        this.setState({
          loading: true,
        });
        this.setState({
          snackbar: {
            open: true,
            title: "Check your connection !",
            severity: "error",
          },
        });
      });
  };

  render() {
    const { children } = this.props;
    const { state } = this;
    return (
      <Provider
        value={{
          ...state,
          onClose: this.onClose,
          deleteArticle: this.deleteArticle,
        }}
      >
        {children}
      </Provider>
    );
  }
}

export { Provider, Consumer, ContextType, ArticleProvider };
