import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/styles";
import MaterialTable from "material-table";
import axios from "axios";
import { apiDashManage } from "../../api/api";

const useStyles = makeStyles((theme) => ({
  root: {
    padding: theme.spacing(0.5),
  },
  content: {
    marginTop: theme.spacing(0),
  },
}));

export default function Categeory() {
  const classes = useStyles();
  const [token, setToken] = useState("");
  const [nameCategory, setNameCategory] = useState([]);
  const [state, setState] = useState({
    columns: [{ title: "Name", field: "name" }],
    data: [
      {
        name: "Daun",
      },
      {
        name: "Pohon",
      },
      {
        name: "Lontong",
      },
    ],
  });

  useEffect(() => {
    setToken(localStorage.getItem("token"));
  }, [token]);

  useEffect(() => {
    handleGetCategory();
  }, []);
  const handleGetCategory = () => {
    axios({
      method: "get",
      url: `${apiDashManage + "category"}`,
      headers: {
        Authorization: "Bearer " + token,
      },
    }).then((res) => {
      console.log("respon get category", res.data.categories);
      setNameCategory(res.data.categories);
    });
  };

  return (
    <div className={classes.root}>
      <MaterialTable
        className={classes.content}
        title="Category"
        columns={state.columns}
        data={nameCategory}
        editable={{
          onRowAdd: (newData) =>
            new Promise((resolve) => {
              resolve();
              setState((prevState) => {
                const data = [...prevState.data];
                // data.push(newData);
                axios({
                  method: "post",
                  url: `${apiDashManage + "category"}`,
                  headers: {
                    Authorization: "Bearer " + token,
                  },
                  data: newData,
                }).then((res) => {
                  console.log("masuk category", res);
                });
                return { ...prevState, data };
              });
            }),
          onRowUpdate: (newData, oldData) =>
            new Promise((resolve) => {
              setTimeout(() => {
                resolve();
                if (oldData) {
                  setState((prevState) => {
                    const data = [...prevState.data];
                    data[data.indexOf(oldData)] = newData;
                    return { ...prevState, data };
                  });
                }
              }, 600);
            }),
          onRowDelete: (oldData) =>
            new Promise((resolve) => {
              setTimeout(() => {
                resolve();
                setState((prevState) => {
                  const data = [...prevState.data];
                  data.splice(data.indexOf(oldData), 1);
                  return { ...prevState, data };
                });
              }, 600);
            }),
        }}
        options={{
          headerStyle: {
            fontSize: "15px",
            fontWeight: "bold",
            borderRight: "1px solid #fff",
            background: "#358A7C",
            color: "#fff",
          },
          rowStyle: {
            fontFamily: "Roboto,Helvetica,Arial,sans-serif",
          },
          exportButton: true,
        }}
      />
    </div>
  );
}
