import React, { useState, useEffect, useContext } from "react";
import { makeStyles } from "@material-ui/styles";
import MaterialTable from "material-table";
import { CategoryContext } from "../../context/categoryContext";
import axios from "axios";
import { apiDashManage } from "../../api/api";

const useStyles = makeStyles((theme) => ({
  root: {
    padding: theme.spacing(0.5),
  },
  content: {
    marginTop: theme.spacing(0),
  },
}));

export default function Categeory() {
  const classes = useStyles();
  const [categoryContext, setCategoryContext] = useContext(CategoryContext);
  const [dataProduct, setDataProduct] = useState([]);
  const [id, setId] = useState(null);
  const [category, setCategory] = useState(null);

  var obj = category.reduce(function(acc, cur, i) {
    acc[cur._id] = cur.name;
    return acc;
  }, {});

  const [state, setState] = React.useState({
    columns: [
      { title: "Name", field: "name" },
      { title: "Price", field: "price" },
      { title: "Stock", field: "stock" },
      { title: "Description", field: "description" },
      { title: "Image", field: "imageUrl" },
      {
        title: "Category",
        field: "categoryId",
        lookup: obj,
      },
    ],
    data: [
      {
        name_product: "Daun",
        slug: "Baran",
        price: "Rp. 80.000",
        stock: "sss",
        description: "ddd",
        status: "sac",
        imageUrl: "laks",
        categoryId: "uuuu",
      },
      {
        name_product: "Daun",
        slug: "Baran",
        price: "Rp. 100.000",
        stock: "sss",
        description: "ddd",
        status: "sac",
        imageUrl: "laks",
        categoryId: "uuuu",
      },
      {
        name_product: "Daun",
        slug: "Baran",
        price: "Rp. 95.000",
        stock: "sss",
        description: "ddd",
        status: "sac",
        imageUrl: "laks",
        categoryId: "uuuu",
      },
      {
        name_product: "Daun",
        slug: "Baran",
        price: "Rp. 100.000",
        stock: "sss",
        description: "ddd",
        status: "sac",
        imageUrl: "laks",
        categoryId: "uuuu",
      },
    ],
  });

  useEffect(() => {
    handleGetProduct();
    handleGetCategory();
  }, []);

  // useEffect(() => {
  //   console.log("data categ", categori);
  // }, [categori]);

  const handleGetCategory = () => {
    axios({
      method: "get",
      url: `${apiDashManage + "category"}`,
      headers: {
        Authorization: "Bearer " + localStorage.getItem("token"),
      },
    }).then((res) => {
      console.log("categ", res.data.categories);
      setCategory(res.data.catagories);
    });
  };

  const handleGetProduct = () => {
    axios({
      method: "get",
      url: `${apiDashManage + "product"}`,
      headers: {
        Authorization: "Bearer " + localStorage.getItem("token"),
      },
    }).then((res) => {
      // console.log("res data", res);
      // console.log("product[0", res.data.products[0]);
      setDataProduct(res.data.products);
    });
  };

  return (
    <div className={classes.root}>
      <MaterialTable
        className={classes.content}
        title="Products"
        columns={state.columns}
        data={dataProduct.map(({ categoryId, ...rest }) => {
          return {
            ...rest,
            categoryId: categoryId._id,
            categoryName: categoryId.name,
          };
        })}
        editable={{
          onRowAdd: (newData) =>
            new Promise((resolve) => {
              // setTimeout(() => {
              resolve();
              setState((prevState) => {
                const data = [...prevState.data];
                axios({
                  method: "post",
                  url: `${apiDashManage + "product"}`,
                  headers: {
                    Authorization: "Bearer " + localStorage.getItem("token"),
                  },
                  data: newData,
                }).then((res) => {
                  console.log("ADD DATA", res);
                  handleGetProduct();
                });
                return { ...prevState, data };
              });
              // }, 600);
            }),
          onRowUpdate: (newData, oldData) =>
            new Promise((resolve) => {
              setTimeout(() => {
                resolve();
                if (oldData) {
                  setState((prevState) => {
                    const data = [...prevState.data];
                    axios({
                      method: "put",
                      url: `${apiDashManage + "product"}`,
                      headers: {
                        Authorization:
                          "Bearer " + localStorage.getItem("token"),
                      },
                      data: newData,
                    }).then((res) => {
                      console.log("EDIT DATA", res);
                      handleGetProduct();
                    });
                    return { ...prevState, data };
                  });
                }
              }, 600);
            }),
          onRowDelete: (oldData) =>
            new Promise((resolve) => {
              setTimeout(() => {
                resolve();
                setState((prevState) => {
                  const data = [...prevState.data];
                  axios({
                    method: "delete",
                    url: `${apiDashManage + `${"product/" + oldData._id}`}`,
                    headers: {
                      Authorization: "Bearer " + localStorage.getItem("token"),
                    },
                    data: oldData,
                  }).then((res) => {
                    console.log("Delete DATA", res);
                    handleGetProduct();
                  });
                  return { ...prevState, data };
                });
              }, 600);
            }),
        }}
        options={{
          headerStyle: {
            fontSize: "15px",
            fontWeight: "bold",
            borderRight: "1px solid #fff",
            background: "#358A7C",
            color: "#fff",
          },
          rowStyle: {
            fontFamily: "Roboto,Helvetica,Arial,sans-serif",
          },
          exportButton: true,
        }}
        detailPanel={(rowData) => {
          return (
            <img
              style={{
                width: "25%",
                height: "250px",
                display: "block",
                marginLeft: "auto",
                marginRight: "auto",
                marginTop: "10px",
                marginBottom: "10px",
              }}
              src="https://images.unsplash.com/photo-1495231916356-a86217efff12?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=676&q=80"
            />
          );
        }}
        onRowClick={(event, rowData, togglePanel) => togglePanel()}
      />
    </div>
  );
}
