import React from "react";
import Snackbar from "@material-ui/core/Snackbar";
import MuiAlert from "@material-ui/lab/Alert";
import Card from "@material-ui/core/Card";
import CardHeader from "@material-ui/core/CardHeader";
import CardActions from "@material-ui/core/CardActions";
import CardContent from "@material-ui/core/CardContent";
import CKEditor from "@ckeditor/ckeditor5-react";
import ClassicEditor from "@ckeditor/ckeditor5-build-classic";
import Button from "@material-ui/core/Button";
import { Consumer } from "context/tentangContext";

function Alert(props) {
  return <MuiAlert elevation={6} variant="filled" {...props} />;
}

const ManageTentang = () => {
  return (
    <Consumer>
      {({ handleChange, addTentang, disabled, loading, snackbar, onClose }) => {
        const { vertical, horizontal, open, title, severity } = snackbar;
        return (
          <React.Fragment>
            <Snackbar
              anchorOrigin={{ vertical, horizontal }}
              open={open}
              autoHideDuration={6000}
              onClose={onClose}
              key={vertical + horizontal}
            >
              <Alert onClose={onClose} severity={severity}>
                {title}
              </Alert>
            </Snackbar>
            <Card>
              <CardHeader
                title="Tentang Kami"
                subheader="Manage your tentang kami"
              />
              <CardContent>
                <CKEditor
                  editor={ClassicEditor}
                  onChange={(event, editor) => handleChange(event, editor)}
                />
              </CardContent>

              <CardActions>
                <Button
                  color="primary"
                  variant="contained"
                  style={{ marginTop: 20, marginLeft: 20 }}
                  disabled={disabled}
                  onClick={addTentang}
                >
                  {loading ? "Loading..." : "Tambah"}
                </Button>

                <Button
                  color="primary"
                  variant="contained"
                  style={{ marginTop: 20, marginLeft: 20 }}
                >
                  Ubah
                </Button>
              </CardActions>
            </Card>
          </React.Fragment>
        );
      }}
    </Consumer>
  );
};

export default ManageTentang;
